import React, { useState } from "react";
import List from "./List";

const Items = [];
function delete_clicked(id) {
  let final = Items[id];

  setItem(Items.splice(final, id));
  console.log(Items[id]);

  // setItem((preValues) => {
  //   preValues.filter((item, index) => {
  //     return index !== id;
  //   });
  // });
}
function list_creator(Item, itemIndex) {
  return (
    <List key={itemIndex} id={itemIndex} item={Item} delete={delete_clicked} />
  );
}
function App() {
  const [item, setItem] = useState("");
  const [, onclick] = useState("");
  function item_entered(event) {
    setItem(event.target.value);
  }
  function delete_clicked(id) {
    // let final = Items[id];

    // setItem(Items.splice(final, id));
    // console.log(Items[id]);

    setItem((preValues) => {
      preValues.filter((item, index) => {
        return index !== id;
      });
    });
  }
  function clicked() {
    if (item !== "") {
      Items.push(item);
      onclick(item);
      setItem("");
    }
  }
  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={item_entered} type="text" value={item} />
        <button onClick={clicked}>
          <span>Add</span>
        </button>
      </div>

      {Items.map(list_creator)}
    </div>
  );
}

export default App;
