import React, { useState } from "react";
import List from "./List";

const Items = ["item", "udf"];
function list_creator(Item) {
  return <List item={Item} />;
}
function App() {
  const [item, newitem] = useState("");
  const [click, onclick] = useState(0);
  function item_entered(event) {
    if ({ click } === 1) {
      newitem(event.target.value);
    }
  }

  function clicked() {
    onclick(1);
  }
  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={item_entered} type="text" />
        <button onClick={clicked}>
          <span>Add</span>
        </button>
      </div>
      <h3>{item}</h3>

      {Items.map(list_creator)}
    </div>
  );
}

export default App;
